import React, { useEffect, useState } from "react";
import './css/LoginSection.scss';
import axios from 'axios';
import Header from "./Header.jsx";
import Footer from './Footer.jsx'
import loader from './assets/774.gif'
import { useNavigate } from "react-router-dom";
import { useMachine } from './context.jsx'

/* хук для отслеживания размера экрана */
const useWindowSize = () => {
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
    });

    useEffect(() => {
        const handleResize = () => {
            setWindowSize({
                width: window.innerWidth,
            });
        };

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return windowSize;
};


export default function LoginSection() {
    const { width } = useWindowSize();
    const [InputLogin, setInputLogin] = useState('');
    const [InputPassword, setInputPassword] = useState('');
    const [Token, setToken] = useState('')
    const [err, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const [changeButtonEnter ,setChangeButtonEnter] = useState(false)
    const { setTokenLocal } = useMachine() 
    const navigate = useNavigate();

    const handleChangesInputLogin = (e) => {
        setInputLogin(e.target.value)
        setError()
    }

    const handleChangesInputPassword = (e) => {
        setInputPassword(e.target.value)
        setError()
    }


    const disable = () => {
        if (InputLogin != ''){
            if (InputPassword != ''){
                return false
            }
        }
        return true  
    }

    const tokenRequest = async () => {
        try {
            setLoading(true)
            const response = await axios.post('http://127.0.0.1:8001/api/token/', {
            "username": InputLogin,
            "password": InputPassword
            })
            setToken(response.data)
            setError()
            localStorage.setItem('login', InputLogin)
            localStorage.setItem('Token', response.data.access)
            localStorage.setItem('Role', response.data.role)
            const tokenLocal = localStorage.getItem('Token')
            if (tokenLocal) {
                setTokenLocal(tokenLocal)
                setLoading(false)
                navigate('/home')
            }

        }
        catch (err) {
            setError(err.message)
            setInputLogin('')
            setInputPassword('')
            setLoading(false)
        }
    }

    return (
        <>
        <wrapper className='wrapper'>
        {width <= '360' ? (
            <div>
                <Header/>
                <section className="component-login-section">
                <h3 className='login-section-title'>Авторизация</h3>
                {loading ? (
                        <div className='login-section'>
                            <img src={loader} className="video-loading" autoPlay mute loop/>
                        </div>
                ):(
                    <div className='login-section'>
                        <form className='login-form'>
                            <input 
                                className={`login-form-input ${err ? 'invalid' : ''}`}
                                type='text' 
                                placeholder='Логин'
                                value={InputLogin}
                                onChange={handleChangesInputLogin}>
                            </input>
                            <input 
                                className={`login-form-input ${err ? 'invalid' : ''}`}
                                type='password' 
                                placeholder='Пароль'
                                value={InputPassword}
                                onChange={handleChangesInputPassword}>
                            </input>

                            <button 
                                className={`login-form-button ${changeButtonEnter ? 'action' : ''}`}
                                disabled={disable()} 
                                onMouseEnter={() => setChangeButtonEnter(true)}
                                onMouseLeave={() => setChangeButtonEnter(false)}
                                onClick={(e) => { e.preventDefault(); tokenRequest(); }}>Войти
                            </button>

                        </form>
                    </div>)}
                </section>
                <Footer/>
            </div>
        ):(
        <>
           <section className="component-login-section">
                <h3 className='login-section-title'>Авторизация</h3>
                {loading ? (
                        <div className='login-section'>
                            <img src={loader} className="video-loading" autoPlay mute loop/>
                        </div>
                ):(
                    <div className='login-section'>
                        <form className='login-form'>
                            <input 
                                className={`login-form-input ${err ? 'invalid' : ''}`}
                                type='text' 
                                placeholder='Логин'
                                value={InputLogin}
                                onChange={handleChangesInputLogin}>
                            </input>
                            <input 
                                className={`login-form-input ${err ? 'invalid' : ''}`}
                                type='password' 
                                placeholder='Пароль'
                                value={InputPassword}
                                onChange={handleChangesInputPassword}>
                            </input>

                            <button 
                                className={`login-form-button ${changeButtonEnter ? 'action' : ''}`}
                                disabled={disable()} 
                                onMouseEnter={() => setChangeButtonEnter(true)}
                                onMouseLeave={() => setChangeButtonEnter(false)}
                                onClick={(e) => { e.preventDefault(); tokenRequest(); }}>Войти
                            </button>

                        </form>
                    </div>)}
                </section> 
        </>
    )}
        </wrapper>
        </>
    )
}
