import React, { useState } from "react";
import { useEffect } from "react";
import { useMachine } from './context.jsx';
import { data } from "react-router-dom";

export default function TableTo() {
    const storedData = localStorage.getItem('DataTo')
    const [dataTo, setDataTo] = useState([])
    const { machine } = useMachine();


    useEffect(() => {
        if (storedData && machine) {
            try {
                const parsedData = JSON.parse(storedData);
                setDataTo(Array.isArray(parsedData) ? parsedData : [parsedData]);
                console.log('table', machine)
            } catch (error) {
                console.error("Error localStorage:", error);
                setDataTo([]);
            }
        }
    }, [storedData, machine]);

    useEffect(() => {
        if (dataTo.length > 0) {
            localStorage.setItem('typeTo', dataTo[0].TypeTO || "Нет данных");
        }
    }, [dataTo]);

    return (
        <table>
                <thead>
                    <tr>
                        <th>Вид ТО</th>
                        <th>Дата проведения ТО</th>
                        <th>Наработка, м/час</th>
                        <th>№ заказ-наряда</th>
                        <th>Дата заказ-наряда</th>
                        <th>Организация, проводившая ТО</th>
                        <th>Машина</th>
                        <th>Сервисная компания</th>
                    </tr>
                </thead>                      
                <tbody>        
                {dataTo && dataTo.length > 0 ? (
                    dataTo
                        .filter((item) => item.model === machine) // Фильтруем данные по модели
                        .map((value, index) => (
                            <tr key={index}>
                                {Object.entries(value).map(([key, data], idx) => (
                                    <td key={idx}>
                                        {data === null ? "Нет данных" : data}
                                    </td>
                                ))}
                            </tr>
                        ))
                ) : (
                    <tr>
                        <td colSpan="100%">Нет данных</td> {/* Добавлено для отображения пустой таблицы */}
                    </tr>
                )}
                </tbody>
            </table>
    )
}
