import React, { useState } from "react";
import { useEffect } from "react";
import { useMachine } from "./context";
import loader from './assets/775.gif'

export default function TableTo() {
    const storedData = localStorage.getItem('DataRec')
    const [dataRec, setDataRec] = useState([])
    const { filterNameItemLocal } = useMachine();
    const { filterItemLocal } = useMachine();
    const [hoveredValue, setHoveredValue] = useState()
    const [loading, setLoading] = useState(false)

    const handleClick = (key, value) => {
        setHoveredValue(value);
        InfoRequestDec(key, value)
    };


    const handleCloseClick = () => {
        setHoveredValue(null);
    }

    useEffect(() => {
        if (storedData) {
            try {
                const parsedData = JSON.parse(storedData);
                setDataRec(Array.isArray(parsedData) ? parsedData : [parsedData]);
            } catch (error) {
                console.error("Error localStorage:", error);
                setDataRec([]);
            }
        }
    }, [storedData]);

        /* получение данных о Рекламациях */
        const InfoRequestRec = async () => {
            try {
                if (!eterationRecApi) {
                    setLoading(true)
                    const response = await axios.get('http://127.0.0.1:8001/api/reclamation/', {
                        headers: {
                            Authorization: `Bearer ${token}`
                        }
                    }) 
                    if (response.data && Array.isArray(response.data)) {
                        const dataRec = response.data; 
                        localStorage.setItem('DataRec', JSON.stringify(dataRec)); 
                        setInfoRec(dataRec);
                        setLoading(false)
                        setEterationRecApi(true)
                    } else {
                        setError('No valid data received');
                        setInfoRec([]); 
                        setLoading(false)
                        setEterationRecApi(false)
                    }
                }
            }
            catch (err) {
                setError(err.message)
                setLoading(false)  
                setEterationRecApi(false) 
            }
        }


    return (
        <>
        <table>
                <thead>
                    <tr>
                        <th>Дата отказа</th>
                        <th>Наработка, м/час</th>
                        <th>Узел отказа</th>
                        <th>Описание отказа</th>
                        <th>Способ восстановления</th>
                        <th>Используемые запасные части</th>
                        <th>Дата восстановления</th>
                        <th>Время простоя техники</th>
                        <th>Mашина</th>
                        <th>Cервисная компания</th>
                    </tr>
                </thead>                      
                <tbody>        
                    {dataRec && dataRec.length > 0 ? ( 
                        dataRec.map((value, index) => (
                            value[filterItemLocal] == filterNameItemLocal || filterItemLocal == 'none'  ? (
                            <tr key={index}>
                                {Object.entries(value).map(([key, data], idx) => (
                                    <td 
                                        key={idx}
                                        onClick={() => handleClick(key, value)}>
                                        {data === null ? "Нет данных" : data}
                                    </td>
                                ))}
                            </tr>
                        ):(<></>)))
                    ) : (
                        <tr></tr>
                    )}
                </tbody>
            </table>
            {hoveredValue && dataRec && (
                <div className="details-value">
                   <div className="container-details-view">
                        {!loading ? (
                            <>
                                <p className="name-detail-view">Модель: {dataRec.name || "Имя не найдено"}</p>
                                <p className="decsription-detail-view">Описание: {dataRec.description || "Описание не найдено"}</p>
                                {console.log(dataRec)}
                                <div className="close-button" onClick={handleCloseClick}>Закрыть</div>
                            </>
                        ):(<img src={loader} className="video-loading-delains" autoPlay muted loop />)}
                    </div>
               </div>
            )}
        </>            
    )
}
