import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";

export default function Update() {
    const role = localStorage.getItem('role')
    const token = localStorage.getItem('Token')
    const [typeTo, setTypeTo] = useState('')
    const [updateDataTo, setUpdateDataTo] = useState({})

    const handelChangeStateType = (e) => {
        setTypeTo(e.target.value)
    }

    const UpdateRequest = async (id, data) => {
        try {
            const response = await axios.put(`http://127.0.0.1:8001/api/to/update/${id}`, data, {
                headers: {
                     Authorization: `Bearer ${token}`
                }
            });
            console.log(response)
        }
        catch (err){
            console.log(err.message)
        }
    }

    return (
    <>
        <Header/>
        <main className="update-to">
            <input 
                className='update-tupe-input' 
                placeholder='Тип ТО' 
                type='text' 
                value={typeTo} 
                onChange={handelChangeStateType}>
            </input>
        </main>
        <Footer/>
    </>
    )
}
