import React, { useState } from "react";
import axios from "axios";
import Header from "./Header";
import Footer from "./Footer";

export default function Update() {
    const role = localStorage.getItem('role')
    const token = localStorage.getItem('Token')
    const [typeTo, setTypeTo] = useState('')
    const [dateTo, setDateTo] = useState('')
    const [devTo, setDevTo] = useState('')
    const [orderNum, setOrderNum] = useState('')
    const [dataOrder, setDataOrder] = useState('')
    const [machine, setMachine] = useState('')
    const [updateDataTo, setUpdateDataTo] = useState({})

    const handelChangeStateType = (e) => {
        setTypeTo(e.target.value)
    }
    const handelChangeStateDate = (e) => {
        setDateTo(e.target.value)
    }
    const handelChangeStateDevelopment = (e) => {
        setDevTo(e.target.value)
    }
    const handelChangeStateOrderNum = (e) => {
        setOrderNum(e.target.value)
    }
    const handelChangeStateDataOrder = (e) => {
        setDateTo(e.target.value)
    }
    const handelChangeStateMachine = (e) => {
        setMachine(e.target.value)
    }

    // const UpdateRequest = async (id, data) => {
    //     try {
    //         const response = await axios.put(`http://127.0.0.1:8001/api/to/update/${id}`, data, {
    //             headers: {
    //                  Authorization: `Bearer ${token}`
    //             }
    //         });
    //         console.log(response)
    //     }
    //     catch (err){
    //         console.log(err.message)
    //     }
    // }

    return (
    <>
        <Header/>
        <main className="update-to">
            <input 
                className='update-type-input' 
                placeholder='Тип ТО' 
                type='text' 
                value={typeTo} 
                onChange={handelChangeStateType}>
            </input>
            <input 
                className='update-date-input' 
                placeholder='Дата ТО' 
                type='date' 
                value={dateTo} 
                onChange={handelChangeStateDate}>
            </input>
            <input 
                className='update-dev-input' 
                placeholder='Наработка часов' 
                type="number" 
                value={devTo} 
                onChange={handelChangeStateDevelopment}>
            </input>
            <input 
                className='update-orderNum-input' 
                placeholder='Номер заказа' 
                type='number' 
                value={orderNum} 
                onChange={handelChangeStateOrderNum}>
            </input>
            <input 
                className='update-dataOrder-input' 
                placeholder='Дата заказа' 
                type='date' 
                value={dataOrder} 
                onChange={handelChangeStateDataOrder}>
            </input>
            <input 
                className='update-machine-input' 
                placeholder='Машина' 
                type='text' 
                value={machine} 
                onChange={handelChangeStateMachine}>
            </input>
            <input 
                className='update-serviceCompany-input' 
                placeholder='Сервисная компания' 
                type='text' 
                value={serviceCompany} 
                onChange={handelChangeStateServiceCompany}>
            </input>
        </main>
        <Footer/>
    </>
    )
}
