import React, { useEffect, useState } from "react";
import './css/LoginSection.scss';
import axios from 'axios';
import Header from "./Header.jsx";
import Footer from './Footer.jsx'
import loader from './assets/774.gif'
import { useNavigate } from "react-router-dom";


export default function LoginSection() {

    const [InputLogin, setInputLogin] = useState('');
    const [InputPassword, setInputPassword] = useState('');
    const [Token, setToken] = useState('')
    const [err, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate();

    const handleChangesInputLogin = (e) => {
        setInputLogin(e.target.value)
        setError()
    }

    const handleChangesInputPassword = (e) => {
        setInputPassword(e.target.value)
        setError()
    }

    const disable = () => {
        if (InputLogin != ''){
            if (InputPassword != ''){
                return false
            }
        }
        return true  
    }

    const tokenRequest = async () => {
        try {
            setLoading(true)
            const response = await axios.post('http://127.0.0.1:8001/api/token/', {
            "username": InputLogin,
            "password": InputPassword
            })
            setToken(response.data)
            setError()
            localStorage.setItem('login', InputLogin)
            localStorage.setItem('Token', response.data.access)
            localStorage.setItem('role', response.data.role)
            navigate('/checkresults')
            setLoading(false)
        }
        catch (err) {
            setError(err.message)
            setInputLogin('')
            setInputPassword('')
            setLoading(false)
        }
    }

    return (
        <>
            <Header/>

            <section className="component-login-section">
            <h3 className='login-section-title'>Авторизация</h3>
            {loading ? (
                    <div className='login-section'>
                        <img src={loader} className="video-loading" autoPlay mute loop/>
                    </div>
            ):(
                <div className='login-section'>
                    <form className='login-form'>
                        <input 
                            className={`login-form-input ${err ? 'invalid' : ''}`}
                            type='text' 
                            placeholder='Логин'
                            value={InputLogin}
                            onChange={handleChangesInputLogin}>
                        </input>
                        <input 
                            className={`login-form-input ${err ? 'invalid' : ''}`}
                            type='password' 
                            placeholder='Пароль'
                            value={InputPassword}
                            onChange={handleChangesInputPassword}>
                        </input>

                        <button 
                            className='login-form-button'
                            disabled={disable()} 
                            onClick={(e) => { e.preventDefault(); tokenRequest(); }}>Войти
                        </button>

                    </form>
                </div>)}
            </section>
        <Footer/>
        </>
    )
}
