import './App.css';
import LoginSection from './LoginSection.jsx'
import HomeNoneAuth from './HomeNoneAuth.jsx'
import Home from './Home.jsx'
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import { useMachine } from './context.jsx';


function App() {
  const { token } = useMachine()
  const role = localStorage.getItem('Role')
  const localToken = localStorage.getItem('Token')

  return (
      <BrowserRouter>
      <Routes>
        <Route path='/login' element={token ? <Navigate to="/home" /> : <LoginSection />}></Route>
        <Route path='/home' element={!token ? <HomeNoneAuth/> : <Home />} ></Route>
      </Routes>
      </BrowserRouter>
  );
}

export default App;
